import{_ as a,c as t,o as i,b as e,d as n}from"./chunks/framework.DslyC0Q_.js";const w=JSON.parse('{"title":"Prenatal food insecurity post Hurricane Maria is associated with decreased Veillonella in the infant gut","description":"","frontmatter":{"layout":"paper","title":"Prenatal food insecurity post Hurricane Maria is associated with decreased Veillonella in the infant gut","date":"2020-03-14","authors":["Leyao Wang","David de Ángel Solá","Midnela Acevedo Flores","Andrew Schriefer","Leran Wang","Kamil Gerónimo López","Alison Chang","Barbara Warner","Liang Shan","Lori R Holtz","Nicolás Rosario Matos"],"journal":"Pediatric Research","doi":"10.1038/s41390-020-0839-0","link":"https://www.nature.com/articles/s41390-020-0839-0","image":"/assets/papers/2020_leyaowang_b.webp","keywords":["Hurricane Maria","Food insecurity","Infant gut microbiome","Prenatal exposure","Puerto Rico"],"selected":true,"head":[["link",{"rel":"canonical","href":"https://leyaowanglab.smart.org.cn/papers/2020_leyaowang_b.html"}]]},"headers":[],"relativePath":"papers/2020_leyaowang_b.md","filePath":"papers/2020_leyaowang_b.md"}'),o={name:"papers/2020_leyaowang_b.md"},r=e("h2",{id:"abstract",tabindex:"-1"},[n("Abstract "),e("a",{class:"header-anchor",href:"#abstract","aria-label":'Permalink to "Abstract"'},"​")],-1),s=e("p",null,"Hurricane Maria struck Puerto Rico on 20 September 2017 causing catastrophic devastation. Prolonged shortage of food had been a substantial challenge to the residents after Maria. Experiencing food insecurity in utero has been associated with negative health outcomes later in life. We aim to examine whether there is any alteration in the infant gut microbiome that is associated with prenatal food insecurity.",-1),c=e("p",null,"We established a cohort of infants aged 2–6 months who were exposed in utero to Hurricane Maria near San Juan, Puerto Rico and examined the gut microbiota (n = 29) using 16S ribosomal RNA gene sequencing.",-1),l=e("p",null,"Among the enrolled infants, 30% of their mothers experienced “post-Maria poor access to food” for at least 1 month during pregnancy. The relative abundance of gut Veillonella spp. is significantly decreased among infants who experienced prenatal food insecurity, compared to those who did not (adjusted p = 0.025). There is no significant difference observed by prenatal food insecurity at the microbial community level in this cohort.",-1),d=e("p",null,"Our finding indicated that infants who experienced prenatal food insecurity post hurricane harbor microbial alternations of specific bacterial taxa, which may further influence the microbial maturation and place the individual at a high-risk health trajectory.",-1),h=[r,s,c,l,d];function u(p,f,m,g,_,b){return i(),t("div",null,h)}const x=a(o,[["render",u]]);export{w as __pageData,x as default};
