import{_ as t,c as a,o as i,b as e,d as n}from"./chunks/framework.DWOzEY2-.js";const b=JSON.parse('{"title":"Changes in physical activity and sedentary time among children with asthma during the COVID-19 pandemic and influencing factors","description":"","frontmatter":{"layout":"paper","title":"Changes in physical activity and sedentary time among children with asthma during the COVID-19 pandemic and influencing factors","date":"2022-04-22","authors":["Sandra Lee","Ai Zhang","Lei Liu","Deborah Salvo","Leyao Wang"],"journal":"Journal of Asthma","doi":"10.1080/02770903.2022.2070761","link":"https://www.tandfonline.com/doi/full/10.1080/02770903.2022.2070761","keywords":["Asthma"],"head":[["link",{"rel":"canonical","href":"https://leyaowanglab.smart.org.cn/papers/2023_sandra.html"}]]},"headers":[],"relativePath":"papers/2023_sandra.md","filePath":"papers/2023_sandra.md"}'),s={name:"papers/2023_sandra.md"},r=e("h2",{id:"abstract",tabindex:"-1"},[n("Abstract "),e("a",{class:"header-anchor",href:"#abstract","aria-label":'Permalink to "Abstract"'},"​")],-1),o=e("p",null,"Regular physical activity is essential for asthma control in children, but it remains understudied within the context of COVID-19. Physical activity and sedentary time levels before and during the COVID-19 pandemic among children with asthma were documented and differences by characteristics were explored. Methods",-1),d=e("p",null,"This was a cross-sectional self-administered online survey study of 5- to 17-year-old children with asthma from the United States between December 2020 and April 2021.",-1),c=e("p",null,"This study included 68 children with asthma. Although only 4.6% of the children were fully inactive before the pandemic, this number increased to 24.6% during the survey period (p < 0.001). Children spent significantly less time outdoors and more time in front of screens during the pandemic versus before (p < 0.001). The variety of activities in which children with asthma engaged in during the pandemic was lower than what they used to do prior to the COVID-19 crisis. Boys, Hispanic children, those of low-income households, and those not attending school in-person were significantly associated with less participation in physical activity during the pandemic. Ethnicity remained significantly associated after adjusting for multiple comparisons.",-1),h=e("p",null,"During the COVID-19 pandemic, children with asthma were less active and spent more time in front of screens and less time outdoors. Subgroup analyses revealed individual, parental, and organizational characteristics being associated with differential participation in physical activity, highlighting disparities in opportunities for children with asthma of different circumstances to remain active and healthy during the pandemic. Additional, more robust longitudinal studies are needed to confirm these results.",-1),l=[r,o,d,c,h];function p(m,u,f,g,y,_){return i(),a("div",null,l)}const v=t(s,[["render",p]]);export{b as __pageData,v as default};
