import{_ as a,c as t,o as n,b as e,d as o}from"./chunks/framework.Mj2lhGLF.js";const _=JSON.parse('{"title":"Dynamic contrast optical coherence tomography (DyC-OCT) for label-free live cell imaging","description":"","frontmatter":{"layout":"paper","title":"Dynamic contrast optical coherence tomography (DyC-OCT) for label-free live cell imaging","date":"2024-03-06","authors":["Chao Ren","Senyue Hao","Fei Wang","Abigail Matt","Marcello Magri Amaral","Daniel Yang","Leyao Wang","Chao Zhou"],"journal":"communications biology","doi":"10.1038/s42003-024-05973-5","link":"https://www.nature.com/articles/s42003-024-05973-5","image":"/assets/papers/2024_renchao.webp","keywords":["Optical coherence tomography"],"head":[["link",{"rel":"canonical","href":"https://leyaowanglab.smart.org.cn/papers/2024_renchao.html"}]]},"headers":[],"relativePath":"papers/2024_renchao.md","filePath":"papers/2024_renchao.md"}'),i={name:"papers/2024_renchao.md"},r=e("h2",{id:"abstract",tabindex:"-1"},[o("Abstract "),e("a",{class:"header-anchor",href:"#abstract","aria-label":'Permalink to "Abstract"'},"â€‹")],-1),c=e("p",null,"Dynamic contrast optical coherence tomography (DyC-OCT), an emerging imaging method, utilizes fluctuation patterns in OCT signals to enhance contrast, thereby enabling non-invasive label-free volumetric live cell imaging. In this mini review, we explain the core concepts behind DyC-OCT image formation and its system configurations, serving as practical guidance for future DyC-OCT users. Subsequently, we explore its applications in delivering high-quality, contrast-enhanced images of cellular morphology, as well as in monitoring changes in cellular activity/viability assay experiments.",-1),s=[r,c];function l(h,p,m,g,d,u){return n(),t("div",null,s)}const f=a(i,[["render",l]]);export{_ as __pageData,f as default};
